package main.liuyi.issubsequence;

/**
 * https://leetcode-cn.com/problems/is-subsequence/description/
 * 
 * @author liuyi
 *
 */
public class Issubsequence {
	public static void main(String[] args) {
		System.out.println(new Issubsequence().isSubsequence("leeeeetcode",
				""));
	}
	public boolean isSubsequence(String s, String t) {
		boolean result = false;
		if (s.isEmpty()) {
			result = true;
			return result;
		}
		if (t.isEmpty()) {
			return result;
		}
		int j = 0;
		int sLen = s.length();
		for (int i = 0; i < t.length(); i++) {
			while (j < sLen && t.charAt(i) == s.charAt(j)) {
				System.out.println("t:" + i + t.charAt(i));
				System.out.println("s:" + j + s.charAt(j));
				j++;
				break;
			}
			if (j == sLen) {
				result = true;
				break;
			}
		}
		return result;
	}
}
